INSERT INTO `maoyao`.`user` (`id`,'pro_pump', 'avatar_url','coin', `create_time`, `update_time`, `status`, `nickname`, `intro`) VALUES('123470','0.1', 'http://file.caixinet.com/1_1528176949158_专栏占位图.png','0', CURRENT_TIMESTAMP(), CURRENT_TIMESTAMP(), 1, '刘新泉','1971年出生于辽宁省普兰店市,1995年毕业于鲁迅美术学院国画系,现为职业画家。国内多家专业美术媒体曾作专题推介， 作品被美国、法国、日本、香港等地机构及友人收藏。');
